import React, { useState } from 'react';
import { withRouter } from 'react-router-dom'; // Import withRouter from react-router-dom

function LoginPage({ history }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleLogin = (userType) => {
    // Logic for handling login based on userType
    console.log("User Type:", userType);
    console.log("Username:", username);
    console.log("Password:", password);
    // Redirect to appropriate route based on user type
    history.push(`/${userType}-dashboard`); // Example route: /individual-dashboard, /manager-dashboard, /owner-dashboard
  };

  return (
    <div className="login-page">
      <nav className="navbar">
        <ul>
          <li><button onClick={() => handleLogin('individual')}>Individual</button></li>
          <li><button onClick={() => handleLogin('manager')}>Manager</button></li>
          <li><button onClick={() => handleLogin('owner')}>Owner</button></li>
        </ul>
      </nav>
      <div className="login-form">
        <h2>Login</h2>
        <form onSubmit={(e) => { e.preventDefault(); }}>
          <label>
            Username:
            <input type="text" value={username} onChange={handleUsernameChange} />
          </label>
          <br />
          <label>
            Password:
            <input type="password" value={password} onChange={handlePasswordChange} />
          </label>
          <br />
          <button onClick={() => handleLogin('individual')}>Login</button>
        </form>
        <p>Don't have an account? <a href="/register">Register</a></p>
      </div>
    </div>
  );
}

export default withRouter(LoginPage); // Wrap the component with withRouter
