import React, { useState } from 'react';
import './index.css'; // Import CSS file for styling

function LoginPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('individual');

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleUserTypeChange = (event) => {
    setUserType(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Logic for handling login based on userType
    console.log("User Type:", userType);
    console.log("Username:", username);
    console.log("Password:", password);
  };

  return (
    <div className="login-container">
      {/* Additional card for logo and information */}
      <div className="info-card">
        <h2>Logo</h2>
        <p>Some information about your application</p>
      </div>
      {/* Login card */}
      <div className="login-card">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Username:
            <input type="text" value={username} onChange={handleUsernameChange} />
          </label>
          <br />
          <label>
            Password:
            <input type="password" value={password} onChange={handlePasswordChange} />
          </label>
          <br />
          <div className="user-type-options">
            <label className={userType === 'individual' ? 'active' : ''}>
              <input type="radio" value="individual" checked={userType === 'individual'} onChange={handleUserTypeChange} />
              Individual
            </label>
            <label className={userType === 'manager' ? 'active' : ''}>
              <input type="radio" value="manager" checked={userType === 'manager'} onChange={handleUserTypeChange} />
              Manager
            </label>
            <label className={userType === 'owner' ? 'active' : ''}>
              <input type="radio" value="owner" checked={userType === 'owner'} onChange={handleUserTypeChange} />
              Owner
            </label>
          </div>
          <br />
          <button type="submit">Login</button>
        </form>
        <p>Don't have an account? <a href="/register">Register</a></p>
      </div>
    </div>
  );
}

export default LoginPage;
